import React, { Component } from 'react';
import './Training.scss';

import config from 'trainingConfig';

class Training extends Component {
  static displayName = 'Training';

  render() {
    const filteredTraining = config.filter(ele => !ele.hidden);
    const sortedAndFilteredTraining = filteredTraining.sort((a, b) => {
      return a.course.title.localeCompare(b.course.title);
    });

    const training = sortedAndFilteredTraining.map((ele, idx) => {
      const instructors = ele.instructors.map((el, idx) => {
        return (
          <div className='training-instructor' key={idx}>
            <Link to={{ 
              pathname: '/bio/',
              state: {
                name: el.name,
                bio: el.bio,
                twitter: el.twitter
              }
            }}>
              {el.name}
            </Link>
          </div>
        );
      })


      return (
        <div className='training-card'
          key={`${ele}-${idx}`}>
          {ele.course.title}
          {instructors}
        </div>
      );
    });

    return (
      <div className=''>
        <div className='container'>
          {training}
        </div>
      </div>
    );
  }
}

export default Training;
