import React, { Component } from 'react';
import Modal from '../../components/Modal/Modal';
import './TalksSchedule.scss';

import config from 'agendaConfig';


export default class TalksSchedule extends Component {
  static displayName = 'TalksSchedule';

  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      dayIndex: 0,
      showTabNum: 0,
      modal: {
        title: '',
        description: '',
      }
    };
  }

  toggleScheduleDate = (index) => {
    this.setState({
      dayIndex: index,
      showTabNum: index
    });
  };

  popModal = (title, description) => e => {
    this.setState({
      modal:{
        title: title,
        description: description,
      }
    });
    this.toggleModal();
  }

  toggleModal = () => {
    this.setState({
      isOpen: !this.state.isOpen,
    });
  }

  render() {
    const dayOfWeek = config[this.state.dayIndex].dayOfWeek;

    const scheduleTabs = config.map((ele, index) => {
      return (
        <li>
          <input type='radio'
            onChange={() => {this.toggleScheduleDate(index)}}
            name='schedule-tabs'
            id={`tab${index}`}
            checked={index === this.state.showTabNum ? true : false} />
            <label htmlFor={`tab${index}`}>{ele.dayOfWeek}<span>{ele.date}</span></label>
        </li>
      );
    });

    const tabsHeader = (
      <>
        <ul className='tabs'>
          {scheduleTabs}
        </ul>
        <div className='schedule-heading'>{`Tentative ${dayOfWeek} Speaking Schedule`}</div>
        <div className='tz-note'>Note: All times listed are in Central Time.</div>
      </>
    );


    const trackHeaders = config[this.state.dayIndex].roomsInfo.map((ele, index) => {
      return (
        <div className='track' style={{gridColumn: index+1, gridRow: 1}}>{ele.roomName}</div>
      );
    });

    let rows = 2;
    const talksGrid = config[this.state.dayIndex].talks.map((ele, index) => {
      const totalRows = config[this.state.dayIndex].roomsInfo.length;
      const gridColumnStart = ele.roomIndex[0] + 1;
      const gridColumnEnd = ele.roomIndex[ele.roomIndex.length - 1] + 2;
      const gridColumn = `${gridColumnStart} / ${gridColumnEnd}`;

      const gridRow = ele.minutes >= 42 ? `${rows} / ${rows + 2}` : `${rows}`; 

      if (gridColumnEnd - 1 >= config[this.state.dayIndex].roomsInfo.length && ele.minutes < 42) {
        rows++;
      } else if (gridColumnEnd - 1 >= config[this.state.dayIndex].roomsInfo.length && ele.minutes >= 42) {
        rows+= 2;
      }

      return (
        <a style={{gridColumn: gridColumn, gridRow: gridRow}}
          onClick={this.popModal(ele.talkTitle, ele.talkDescription)}>
          <div className='box'>
            <span className='talk-time'>{ele.time}</span>
            <span>{rows} {gridColumnEnd} {gridRow}</span>
            <span>{ele.talkTitle} {gridColumnEnd} {config[this.state.dayIndex].roomsInfo.length} {gridRow}</span>
          </div>
        </a>
      );
    });

    let gridTemplateRows = `42px `.repeat(rows - 1);

    const gridTemplateColumnsPercentage = (100 / config[this.state.dayIndex].roomsInfo.length).toFixed(1);
    const gridTemplateColumnsString = `calc(${gridTemplateColumnsPercentage}% - 10px) `;
    const gridTemplateColumns = `${gridTemplateColumnsString.repeat(config[this.state.dayIndex].roomsInfo.length)}`;

    return (
      <div className='schedule-talks'>

        <Modal show={this.state.isOpen}
          onClose={this.toggleModal}
          title={this.state.modal.title}
        >
          <div className='modal-content'>
            <div className='modal-description'>
              {this.state.modal.description}
            </div>
          </div>         
        </Modal>





        {tabsHeader}
        <div className={`grid-wrapper`}
          style={{gridTemplateColumns: gridTemplateColumns, gridTemplateRows: gridTemplateRows}}>
          {trackHeaders}
          {talksGrid}

        </div>
      </div>
    )
  }
}
