import React, { Component } from 'react';
import './TalksSchedule.scss';


export default class TalksSchedule extends Component {
  static displayName = 'TalksSchedule';

  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      showFirstDay: true,
      showSecondDay: false,
      showTabNum: 1,
    };
  }

  render() {

    const scheduleConfig = [{
      dayOfWeek: 'Friday',
      date: '1'
    }, {
      dayOfWeek: 'Saturday',
      date: '2'
    }];

    const scheduleTabs = scheduleConfig.map((ele, index) => {
      return (
        <li>
          <input type='radio'
            onChange={() => {this.toggleScheduleDate(index)}}
            name='schedule-tabs'
            id={`schedule-tab-${index}`}
            checked={index === this.state.showTabNum ? true : false} />
            <label htmlFor={`schedule-tab-${index}`}>{ele.dayOfWeek}<span>{ele.date}</span></label>
        </li>
      );
    });


    return (
      <div className='schedule-talks'>

        <ul className='tabs'>
          {scheduleTabs}
        </ul>

        <div className='schedule'>{`Tentative Speaking Schedule`}</div>
      </div>
    )
  }
}
