import React, { Component } from 'react';
import './TalksSchedule.scss';


export default class TalksSchedule extends Component {
  static displayName = 'TalksSchedule';

  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      dayIndex: 0,
      showTabNum: 0,
    };
  }

  toggleScheduleDate = (index) => {
    this.setState({
      dayIndex: index,
      showTabNum: index
    });
  };


  render() {

    const scheduleConfig = [{
      dayOfWeek: 'Friday',
      date: '1',
      roomsInfo: [{
        roomName: 'Room 1'
      }, {
        roomName: 'Room 2'
      }, {
        roomName: 'Room 3'
      }],
      talksGrid: [{
        time: '0730',
        roomIndex: [0, 1, 2],
        talkTitle: 'Registration and Rooms Open',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0850',
        roomIndex: [0, 1, 2],
        talkTitle: 'Opening Remarks',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0900',
        roomIndex: [0, 1, 2],
        talkTitle: 'Keynote: Joe Grand',
        authors: [{
          name: 'Joe Grand',
        }]
      },{
        time: '1000',
        roomIndex: [0, 1, 2],
        talkTitle: 'Break.  Chris Eagle Book Signing',
        authors: [{
          name: 'Chris Eagle',
        }]
      },{
        time: '1015',
        roomIndex: [0],
        talkTitle: 'Breaking into Cloud Wallets: Findings from 3 years spent Hacking Crypto Web Apps',
        authors: [{
          name: 'Sam Curry',
        }]
      },{
        time: '1015',
        roomIndex: [1, 2],
        talkTitle: 'Learning to Speak ACARS Using ELK: Using SDR and ELK to Capture and Understand Aviation Data Links',
        authors: [{
          name: 'Carl Schuett',
        },
        {
          name: 'Jesse Young',
        }]
      }]
    }, {
      dayOfWeek: 'Saturday',
      date: '2'
    }];

    const dayOfWeek = scheduleConfig[this.state.dayIndex].dayOfWeek;

    const scheduleTabs = scheduleConfig.map((ele, index) => {
      return (
        <li>
          <input type='radio'
            onChange={() => {this.toggleScheduleDate(index)}}
            name='schedule-tabs'
            id={`tab${index}`}
            checked={index === this.state.showTabNum ? true : false} />
            <label htmlFor={`tab${index}`}>{ele.dayOfWeek}<span>{ele.date}</span></label>
        </li>
      );
    });

    const tabsHeader = (
      <>
        <ul className='tabs'>
          {scheduleTabs}
        </ul>
        <div className='schedule-heading'>{`Tentative ${dayOfWeek} Speaking Schedule`}</div>
        <div className='tz-note'>Note: All times listed are in Central Time.</div>
      </>
    );

    const scheduleGrid = scheduleConfig.map((ele, index) => {
      return (
        <div className={`grid-wrapper-index`}>
          <div className="track">Bat of Doom</div>
          <div className="track">Terrified Chipmunk</div>

        </div>
      );
    });


    return (
      <div className='schedule-talks'>
        {tabsHeader}
      </div>
    )
  }
}
