import Modal from '../../components/Modal/Modal';
import './TalksSchedule.scss';


export default class TalksSchedule extends Component {
  static displayName = 'TalksSchedule';

  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      dayIndex: 0,
      showTabNum: 0,
      modal: {
        title: '',
      }
    };
  }

  toggleScheduleDate = (index) => {
    this.setState({
      dayIndex: index,
      showTabNum: index
    });
  };

  popModal = (title) => e => {
    this.setState({
      modal:{
        title: title,
      }
    });
    this.toggleModal();
  }

  toggleModal = () => {
    this.setState({
      isOpen: !this.state.isOpen,
    });
  }

  render() {

