        },
        authors: [{
          name: 'Joe Gray',
        }]
      }]
    }];

    const dayOfWeek = scheduleConfig[this.state.dayIndex].dayOfWeek;

    const scheduleTabs = scheduleConfig.map((ele, index) => {
      return (
        <li>
          <input type='radio'
            onChange={() => {this.toggleScheduleDate(index)}}
            name='schedule-tabs'
            id={`tab${index}`}
            checked={index === this.state.showTabNum ? true : false} />
            <label htmlFor={`tab${index}`}>{ele.dayOfWeek}<span>{ele.date}</span></label>
        </li>
      );
    });

    const tabsHeader = (
      <>
        <ul className='tabs'>
          {scheduleTabs}
        </ul>
        <div className='schedule-heading'>{`Tentative ${dayOfWeek} Speaking Schedule`}</div>
        <div className='tz-note'>Note: All times listed are in Central Time.</div>
      </>
    );


    const trackHeaders = scheduleConfig[this.state.dayIndex].roomsInfo.map((ele, index) => {
      return (
        <div className='track'>{ele.roomName}</div>
      );
    });

    let gridTemplateRows = '42px ';

    const talksGrid = scheduleConfig[this.state.dayIndex].talksGrid.map((ele, index) => {
      const gridColumnStart = ele.roomIndex[0] + 1;
      const gridColumnEnd = ele.roomIndex[ele.roomIndex.length - 1] + 2;
      const gridColumn = `${gridColumnStart} / ${gridColumnEnd}`;

      const gridRowSize = ele.minutes >= 42 ? '112px' : '42px'; 
      gridTemplateRows += `${gridRowSize} `;
      return (
        <a style={{gridColumn: gridColumn}}
          onClick={this.popModal(ele.talkTitle)}>
          <div className='box'>
            <span className='talk-time'>{ele.time}</span>
            <span>{ele.talkTitle}</span>
          </div>
        </a>
      );
    });

    const gridTemplateColumnsPercentage = (100 / scheduleConfig[this.state.dayIndex].roomsInfo.length).toFixed(1);
    const gridTemplateColumnsString = `calc(${gridTemplateColumnsPercentage}% - 10px) `;
    const gridTemplateColumns = `${gridTemplateColumnsString.repeat(scheduleConfig[this.state.dayIndex].roomsInfo.length)}`;
    gridTemplateRows += `30px`;

    return (
      <div className='schedule-talks'>

        <Modal show={this.state.isOpen}
          onClose={this.toggleModal}
          title={this.state.modal.title}
        >
          <div className='modal-content'>
          Test
          </div>         
        </Modal>






        {tabsHeader}
        <div className={`grid-wrapper`}
          style={{gridTemplateColumns: gridTemplateColumns, 
            gridTemplateRows: gridTemplateRows}}>
          {trackHeaders}

          {talksGrid}

        </div>
      </div>
    )
  }
