import React, { Component } from 'react';
import './TalksSchedule.scss';


export default class TalksSchedule extends Component {
  static displayName = 'TalksSchedule';

  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      dayIndex: 0,
      showTabNum: 0,
    };
  }

  toggleScheduleDate = (index) => {
    this.setState({
      dayIndex: index,
      showTabNum: index
    });
  };


  render() {

    const scheduleConfig = [{
      dayOfWeek: 'Friday',
      date: '1',
      roomsInfo: [{
        roomName: 'Bat of Doom'
      }, {
        roomName: 'Terrified Chipmunk'
      }],
      talksGrid: [{
        time: '0730',
        minutes: '0',
        roomIndex: [0, 1],
        talkTitle: 'Registration and Rooms Open',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0850',
        minutes: '10',
        roomIndex: [0, 1],
        talkTitle: 'Opening Remarks',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0900',
        minutes: '60',
        roomIndex: [0, 1],
        talkTitle: 'Keynote: Joe Grand',
        authors: [{
          name: 'Joe Grand',
        }]
      },{
        time: '1000',
        minutes: '15',
        roomIndex: [0, 1],
        talkTitle: 'Break.  Chris Eagle Book Signing',
        authors: [{
          name: 'Chris Eagle',
        }]
      },{
        time: '1015',
        minutes: '60',
        roomIndex: [0],
        talkTitle: 'Breaking into Cloud Wallets: Findings from 3 years spent Hacking Crypto Web Apps',
        authors: [{
          name: 'Sam Curry',
        }]
      },{
        time: '1015',
        minutes: '60',
        roomIndex: [1],
        talkTitle: 'Learning to Speak ACARS Using ELK: Using SDR and ELK to Capture and Understand Aviation Data Links',
        authors: [{
          name: 'Carl Schuett',
        },
        {
          name: 'Jesse Young',
        }]
      }]
    }, {
      dayOfWeek: 'Saturday',
      date: '2',
      roomsInfo: [{
        roomName: 'Bat of Doom'
      }, {
        roomName: 'Terrified Chipmunk'
      }],
      talksGrid: [{
        time: '0730',
        minutes: '0',
        roomIndex: [0, 1],
        talkTitle: 'Registration and Rooms Open',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0850',
        minutes: '10',
        roomIndex: [0, 1],
        talkTitle: 'Opening Remarks',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '0900',
        minutes: '60',
        roomIndex: [0, 1],
        talkTitle: 'Keynote: Jen Easterly (Fireside Chat + Q&A)',
        authors: [{
          name: 'Jen Easterly',
        }]
      },{
        time: '1000',
        minutes: '15',
        roomIndex: [0, 1],
        talkTitle: 'Break. (Room Switch)',
        authors: [{
          name: 'Kernelcon',
        }]
      },{
        time: '1015',
        minutes: '60',
        roomIndex: [0],
        talkTitle: 'The Hand That Feeds: How to Misuse Kubernetes',
        authors: [{
          name: 'Lewis Denham-Parry',
        }]
      },{
        time: '1015',
        minutes: '60',
        roomIndex: [1],
        talkTitle: 'NetflOSINT: Taking an often-overlooked data source and operationalizing it',
        authors: [{
          name: 'Joe Gray',
        }]
      }]
    }];

    const dayOfWeek = scheduleConfig[this.state.dayIndex].dayOfWeek;

    const scheduleTabs = scheduleConfig.map((ele, index) => {
      return (
        <li>
          <input type='radio'
            onChange={() => {this.toggleScheduleDate(index)}}
            name='schedule-tabs'
            id={`tab${index}`}
            checked={index === this.state.showTabNum ? true : false} />
            <label htmlFor={`tab${index}`}>{ele.dayOfWeek}<span>{ele.date}</span></label>
        </li>
      );
    });

    const tabsHeader = (
      <>
        <ul className='tabs'>
          {scheduleTabs}
        </ul>
        <div className='schedule-heading'>{`Tentative ${dayOfWeek} Speaking Schedule`}</div>
        <div className='tz-note'>Note: All times listed are in Central Time.</div>
      </>
    );


    const trackHeaders = scheduleConfig[this.state.dayIndex].roomsInfo.map((ele, index) => {
      return (
        <div className='track'>{ele.roomName}</div>
      );
    });

    let gridTemplateRows = '';

    const talksGrid = scheduleConfig[this.state.dayIndex].talksGrid.map((ele, index) => {
      const gridColumnStart = ele.roomIndex[0] + 1;
      const gridColumnEnd = ele.roomIndex[ele.roomIndex.length - 1] + 2;
      const gridColumn = `${gridColumnStart} / ${gridColumnEnd}`;

      const gridRowSize = ele.minutes >= 42 ? '112px' : '42px'; 
      return (
        <a style={{gridColumn: gridColumn}}
          onClick=''>
          <div className='box'>
            <span className='talk-time'>{ele.time}</span>
            {/* <span>{ele.talkTitle}</span> */}
            <span>{gridRowSize}</span>
          </div>
        </a>
      );

    });

    const gridTemplateColumnsPercentage = (100 / scheduleConfig[this.state.dayIndex].roomsInfo.length).toFixed(1);
    const gridTemplateColumnsString = `calc(${gridTemplateColumnsPercentage}% - 10px) `;
    const gridTemplateColumns = `${gridTemplateColumnsString.repeat(scheduleConfig[this.state.dayIndex].roomsInfo.length)}`;

    return (
      <div className='schedule-talks'>
        {tabsHeader}
        <div className={`grid-wrapper`}
          style={{gridTemplateColumns: gridTemplateColumns}}>
          {trackHeaders}

          {talksGrid}

        </div>
      </div>
    )
  }
}
